'use client';

import React from 'react';
import { Checkbox as ShadcnCheckbox } from '@/components/ui/checkbox';
import { Label } from '@/components/ui/label';
import { cn } from '@/lib/utils';

/**
 * TDS Checkbox Component
 * Reference: https://tossmini-docs.toss.im/tds-mobile/components/checkbox/
 * 
 * Supports two variants:
 * - Checkbox.Circle: 체크 아이콘이 원으로 감싸진 형태
 * - Checkbox.Line: 체크 아이콘이 단독으로 표현
 */

export interface CheckboxBaseProps {
    /** Controlled: 체크 상태 */
    checked?: boolean;
    /** Uncontrolled: 초기 체크 상태 */
    defaultChecked?: boolean;
    /** 체크 상태 변경 콜백 */
    onCheckedChange?: (checked: boolean) => void;
    /** 비활성화 */
    disabled?: boolean;
    /** 라벨 */
    children?: React.ReactNode;
    /** 추가 className */
    className?: string;
    /** id */
    id?: string;
    /** Checkbox variant (circle maps to rounded-full, line is default square) */
    variant?: 'circle' | 'line';
    /** 라벨 (기존 호환성) */
    label?: string;
    /** native onChange (기존 호환성) */
    onChange?: (e: unknown) => void;
}

const CheckboxBase = ({
    checked,
    defaultChecked,
    onCheckedChange,
    disabled,
    children,
    className,
    id,
    variant = "line",
    label,
    onChange
}: CheckboxBaseProps) => {
    // Handle legacy onChange that might expect event or boolean
    const handleCheckedChange = (chk: boolean) => {
        onCheckedChange?.(chk);
        if (onChange) {
            // emulate trivial event or just call with chk if legacy used boolean
            // @ts-ignore
            onChange(chk);
        }
    };

    const uniqueId = id || `checkbox-${Math.random().toString(36).substr(2, 9)}`;

    return (
        <div className={cn("flex items-center space-x-2", className)}>
            <ShadcnCheckbox
                id={uniqueId}
                checked={checked}
                defaultChecked={defaultChecked}
                onCheckedChange={handleCheckedChange}
                disabled={disabled}
                className={cn(variant === 'circle' && "rounded-full")}
            />
            {(children || label) && (
                <Label
                    htmlFor={uniqueId}
                    className="text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70"
                >
                    {label || children}
                </Label>
            )}
        </div>
    );
};

// Checkbox.Circle - 원형 체크박스
const CheckboxCircle = (props: CheckboxBaseProps) => (
    <CheckboxBase {...props} variant="circle" />
);
CheckboxCircle.displayName = 'Checkbox.Circle';

// Checkbox.Line - 라인 체크박스
const CheckboxLine = (props: CheckboxBaseProps) => (
    <CheckboxBase {...props} variant="line" />
);
CheckboxLine.displayName = 'Checkbox.Line';

/**
 * 기본 Checkbox (기존 호환성 유지 - Circle 스타일 사용)
 * 새로운 코드에서는 Checkbox.Circle 또는 Checkbox.Line 사용 권장
 */
export const Checkbox = (props: CheckboxBaseProps) => {
    return <CheckboxCircle {...props} />; // Default to Circle as per original legacy wrapper
};

// Compound Component Pattern
Checkbox.Circle = CheckboxCircle;
Checkbox.Line = CheckboxLine;

export { CheckboxCircle, CheckboxLine };
export type { CheckboxBaseProps };

import React, { useState, useEffect } from 'react';
import { ChevronDown } from 'lucide-react';
import {
    Collapsible,
    CollapsibleContent,
    CollapsibleTrigger,
} from "@/components/ui/collapsible"
import { cn } from '@/lib/utils';
import { Button } from '@/components/ui/button';

interface CollapseProps {
    label: string;
    children: React.ReactNode;
    isOpen: boolean;
    onToggle: () => void;
    className?: string;
    rightElement?: React.ReactNode;
}

export const Collapse = ({ label, children, isOpen, onToggle, className, rightElement }: CollapseProps) => {
    return (
        <Collapsible
            open={isOpen}
            onOpenChange={onToggle}
            className={cn("w-full border rounded-lg bg-card text-card-foreground shadow-sm", className)}
        >
            <div className="flex items-center justify-between px-4 py-3">
                <div className="flex items-center gap-2 flex-1">
                    <span className="font-semibold text-sm">{label}</span>
                </div>
                <div className="flex items-center gap-2">
                    {rightElement}
                    <CollapsibleTrigger asChild>
                        <Button variant="ghost" size="sm" className="w-9 p-0">
                            <ChevronDown className={cn("h-4 w-4 transition-transform duration-200", isOpen && "rotate-180")} />
                            <span className="sr-only">Toggle</span>
                        </Button>
                    </CollapsibleTrigger>
                </div>
            </div>
            <CollapsibleContent className="px-4 pb-4">
                {children}
            </CollapsibleContent>
        </Collapsible>
    );
};

import React from 'react';
import {
    Select as ShadcnSelect,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue,
} from "@/components/ui/select"
import { Label } from '@/components/ui/label';
import { cn } from '@/lib/utils';
import { useInvitationStore } from '@/store/useInvitationStore';

interface Option<T> {
    label: string;
    value: T;
}

interface SelectProps<T> {
    value: T;
    options: readonly Option<T>[];
    onChange: (value: T) => void;
    placeholder?: string;
    className?: string;
    labelClassName?: string;
    label?: string; // Add label support if needed or inferred
}

export const Select = <T extends string | number>({
    value,
    options,
    onChange,
    placeholder,
    className = "",
    labelClassName = "",
    label
}: SelectProps<T>) => {
    // We assume T matches string or number which can be converted to string for Select value
    const stringValue = String(value);

    const handleValueChange = (val: string) => {
        // Find orig option to get correct type value back?
        const opt = options.find(o => String(o.value) === val);
        if (opt) {
            onChange(opt.value);
        }
    };

    return (
        <div className={cn("flex flex-col gap-1.5", className)}>
            {label && <Label className={labelClassName}>{label}</Label>}
            <ShadcnSelect value={stringValue} onValueChange={handleValueChange}>
                <SelectTrigger className="w-full">
                    <SelectValue placeholder={placeholder} />
                </SelectTrigger>
                <SelectContent>
                    {options.map((option, idx) => (
                        <SelectItem key={`${String(option.value)}-${idx}`} value={String(option.value)}>
                            {option.label}
                        </SelectItem>
                    ))}
                </SelectContent>
            </ShadcnSelect>
        </div>
    );
};

import React, { useState } from 'react';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { cn } from '@/lib/utils';

interface TextFieldProps extends React.InputHTMLAttributes<HTMLInputElement> {
    label?: string;
    helpText?: React.ReactNode;
    hasError?: boolean;
    right?: React.ReactNode;
    containerClassName?: string;
}

export const TextField = React.forwardRef<HTMLInputElement, TextFieldProps>(
    ({ className, containerClassName, style, label, helpText, hasError, right, disabled, onFocus, onBlur, ...props }, ref) => {
        const [isFocused, setIsFocused] = useState(false);

        const handleFocus = (e: React.FocusEvent<HTMLInputElement>) => {
            setIsFocused(true);
            onFocus?.(e);
        };

        const handleBlur = (e: React.FocusEvent<HTMLInputElement>) => {
            setIsFocused(false);
            onBlur?.(e);
        };

        const id = props.id || props.name;

        return (
            <div className={cn("flex flex-col gap-1.5", containerClassName)}>
                {label && (
                    <Label
                        htmlFor={id}
                        className={cn(hasError && "text-destructive")}
                    >
                        {label}
                    </Label>
                )}

                <div className="relative">
                    <Input
                        ref={ref}
                        disabled={disabled}
                        className={cn(
                            hasError && "border-destructive focus-visible:ring-destructive",
                            right && "pr-10",
                            className
                        )}
                        onFocus={handleFocus}
                        onBlur={handleBlur}
                        style={style}
                        {...props}
                    />
                    {right && (
                        <div className="absolute right-3 top-1/2 -translate-y-1/2 text-muted-foreground">
                            {right}
                        </div>
                    )}
                </div>

                {helpText && (
                    <p className={cn("text-sm text-muted-foreground", hasError && "text-destructive")}>
                        {helpText}
                    </p>
                )}
            </div>
        );
    }
);

TextField.displayName = 'TextField';

import React, { useState } from 'react';
import { CreditCard, Plus, Trash2, ChevronDown } from 'lucide-react';
import { useInvitationStore } from '@/store/useInvitationStore';
import { AccordionItem } from '../AccordionItem';
import { TextField } from '../TextField';
import { SegmentedControl } from '../SegmentedControl';
import { Field } from '../Field';
import styles from './AccountsSection.module.scss';
import { cn } from '@/lib/utils';

interface SectionProps {
    isOpen: boolean;
    onToggle: () => void;
}

export default function AccountsSection({ isOpen, onToggle }: SectionProps) {
    const {
        accounts, setAccounts,
        accountsTitle, setAccountsTitle,
        accountsDescription, setAccountsDescription,
        accountsGroomTitle, setAccountsGroomTitle,
        accountsBrideTitle, setAccountsBrideTitle,
        accountsColorMode, setAccountsColorMode
    } = useInvitationStore();

    const [expandedId, setExpandedId] = useState<string | null>(null);

    const handleAddAccount = () => {
        const newAccount = {
            id: Math.random().toString(36).substr(2, 9),
            type: 'groom' as const,
            relation: '본인' as const,
            bank: '',
            accountNumber: '',
            holder: ''
        };
        setAccounts([...accounts, newAccount]);
        setExpandedId(newAccount.id);
    };

    const handleUpdateAccount = (id: string, data: Partial<any>) => {
        setAccounts(accounts.map(acc => acc.id === id ? { ...acc, ...data } : acc));
    };

    const handleRemoveAccount = (id: string) => {
        setAccounts(accounts.filter(acc => acc.id !== id));
    };

    return (
        <AccordionItem
            title="축의금 및 계좌번호"
            icon={CreditCard}
            isOpen={isOpen}
            onToggle={onToggle}
            isCompleted={accounts.length > 0}
        >
            <div className={styles.container}>
                {/* Titles */}
                <Field label="페이지 문구">
                    <div className={styles.optionWrapper}>
                        <TextField
                            label="메인 제목"
                            placeholder="예: 마음 전하실 곳"
                            value={accountsTitle}
                            onChange={(e) => setAccountsTitle(e.target.value)}
                        />
                        <TextField
                            label="대표 설명"
                            placeholder="축하의 마음을 담아..."
                            value={accountsDescription}
                            onChange={(e) => setAccountsDescription(e.target.value)}
                            multiline
                            rows={3}
                        />
                    </div>
                </Field>

                {/* Account List */}
                <Field label="계좌 리스트">
                    <div className={styles.accountList}>
                        {accounts.map((acc) => (
                            <div key={acc.id} className={styles.accountItem}>
                                <div
                                    className={styles.accountHeader}
                                    onClick={() => setExpandedId(expandedId === acc.id ? null : acc.id)}
                                >
                                    <div className={styles.accountTitle}>
                                        <span className={cn(styles.badge, acc.type === 'groom' ? styles.groom : styles.bride)}>
                                            {acc.type === 'groom' ? '신랑측' : '신부측'}
                                        </span>
                                        <span className={styles.name}>
                                            {acc.holder || '새 계좌'} ({acc.relation})
                                        </span>
                                    </div>
                                    <ChevronDown
                                        size={18}
                                        className={cn(styles.chevron, expandedId === acc.id && styles.expanded)}
                                    />
                                </div>

                                {expandedId === acc.id && (
                                    <div className={styles.accountBody}>
                                        <div className={styles.fieldRow}>
                                            <div className={styles.flex2}>
                                                <SegmentedControl
                                                    value={acc.type}
                                                    options={[
                                                        { label: '신랑측', value: 'groom' },
                                                        { label: '신부측', value: 'bride' },
                                                    ]}
                                                    onChange={(val) => handleUpdateAccount(acc.id, { type: val as any })}
                                                />
                                            </div>
                                            <div className={styles.flex3}>
                                                <TextField
                                                    placeholder="관계 (예: 본인, 아버지)"
                                                    value={acc.relation}
                                                    onChange={(e) => handleUpdateAccount(acc.id, { relation: e.target.value })}
                                                />
                                            </div>
                                        </div>

                                        <div className={styles.fieldRow}>
                                            <div className={styles.flex1}>
                                                <TextField
                                                    label="은행"
                                                    placeholder="예: 신한"
                                                    value={acc.bank}
                                                    onChange={(e) => handleUpdateAccount(acc.id, { bank: e.target.value })}
                                                />
                                            </div>
                                            <div className={styles.flex1}>
                                                <TextField
                                                    label="예금주"
                                                    placeholder="성함"
                                                    value={acc.holder}
                                                    onChange={(e) => handleUpdateAccount(acc.id, { holder: e.target.value })}
                                                />
                                            </div>
                                        </div>

                                        <TextField
                                            label="계좌번호"
                                            placeholder="숫자만 입력"
                                            value={acc.accountNumber}
                                            onChange={(e) => handleUpdateAccount(acc.id, { accountNumber: e.target.value.replace(/[^0-9]/g, '') })}
                                        />

                                        <button
                                            className={styles.removeBtn}
                                            onClick={() => handleRemoveAccount(acc.id)}
                                        >
                                            <Trash2 size={14} />
                                            <span>계좌 삭제</span>
                                        </button>
                                    </div>
                                )}
                            </div>
                        ))}

                        <button
                            className={styles.addBtn}
                            onClick={handleAddAccount}
                        >
                            <Plus size={18} />
                            <span>계좌 추가하기</span>
                        </button>
                    </div>
                </Field>

                {/* Appearance Settings */}
                <Field label="표시 설정">
                    <div className={styles.optionWrapper}>
                        <TextField
                            label="신랑측 그룹 제목"
                            placeholder="신랑 측 마음 전하실 곳"
                            value={accountsGroomTitle}
                            onChange={(e) => setAccountsGroomTitle(e.target.value)}
                        />
                        <TextField
                            label="신부측 그룹 제목"
                            placeholder="신부 측 마음 전하실 곳"
                            value={accountsBrideTitle}
                            onChange={(e) => setAccountsBrideTitle(e.target.value)}
                        />
                        <Field label="색상 모드" className="mt-2">
                            <SegmentedControl
                                value={accountsColorMode}
                                options={[
                                    { label: '강조', value: 'accent' },
                                    { label: '은은하게', value: 'subtle' },
                                    { label: '화이트', value: 'white' },
                                ]}
                                onChange={(val) => setAccountsColorMode(val as any)}
                            />
                        </Field>
                    </div>
                </Field>
            </div>
        </AccordionItem>
    );
}

import React, { useMemo, useCallback, useState, useRef } from 'react';
import Image from 'next/image';
import { Image as ImageIcon, Plus, Info, X, GripVertical } from 'lucide-react';
import { useInvitationStore } from '@/store/useInvitationStore';
import { Field } from '../Field';
import { Label } from '../Label';
import { SegmentedControl } from '../SegmentedControl';
import { AccordionItem } from '../AccordionItem';
import { TextField } from '../TextField';
import { Switch } from '../Switch';
import { cn } from '@/lib/utils';

import {
    DndContext,
    closestCenter,
    KeyboardSensor,
    PointerSensor,
    useSensor,
    useSensors,
    DragEndEvent,
    DragOverlay,
    DragStartEvent,
    defaultDropAnimationSideEffects,
} from '@dnd-kit/core';
import {
    arrayMove,
    SortableContext,
    sortableKeyboardCoordinates,
    rectSortingStrategy,
    useSortable
} from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import styles from './GallerySection.module.scss';

interface SectionProps {
    isOpen: boolean;
    onToggle: () => void;
}

interface SortableItemProps {
    id: string;
    url: string;
    onRemove: (id: string) => void;
    isDragging?: boolean;
}

const SortableItem = React.memo(function SortableItem({ id, url, onRemove, isDragging }: SortableItemProps) {
    const {
        attributes,
        listeners,
        setNodeRef,
        transform,
        transition,
    } = useSortable({ id });

    const style = {
        transform: CSS.Translate.toString(transform),
        transition,
        touchAction: 'none',
    };

    const isUploading = url.startsWith('blob:');

    return (
        <div
            ref={setNodeRef}
            style={style}
            className={cn(
                styles.sortableItem,
                isDragging && styles.dragging,
                isUploading && styles.uploading
            )}
        >
            <Image
                src={url}
                alt="Gallery"
                fill
                unoptimized
                className={cn(styles.image, isUploading && styles.imageUploading)}
            />

            {isUploading && (
                <div className={styles.uploadingOverlay}>
                    <div className={styles.spinner} />
                </div>
            )}

            <div className={styles.gradientOverlay} />

            <button
                className={styles.deleteBtn}
                onClick={(e) => {
                    e.stopPropagation();
                    onRemove(id);
                }}
                onPointerDown={(e) => e.stopPropagation()}
            >
                <X size={12} />
            </button>

            <div
                {...attributes}
                {...listeners}
                className={styles.dragHandle}
            />
        </div>
    );
});

const GallerySection = React.memo<SectionProps>(function GallerySection({ isOpen, onToggle }) {
    const {
        gallery, setGallery,
        galleryTitle, setGalleryTitle,
        galleryType, setGalleryType,
        galleryPopup, setGalleryPopup,
        galleryPreview, setGalleryPreview,
        galleryFade, setGalleryFade,
        galleryAutoplay, setGalleryAutoplay,
        theme
    } = useInvitationStore();

    const fileInputRef = useRef<HTMLInputElement>(null);
    const [activeId, setActiveId] = useState<string | null>(null);

    const sensors = useSensors(
        useSensor(PointerSensor, {
            activationConstraint: {
                distance: 8,
            },
        }),
        useSensor(KeyboardSensor, {
            coordinateGetter: sortableKeyboardCoordinates,
        })
    );

    const handleUploadClick = () => fileInputRef.current?.click();

    const handleFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
        const filesToUpload = Array.from(e.target.files || []);
        if (filesToUpload.length === 0) return;

        const MAX_TOTAL = 30;
        const currentCount = gallery.length;

        if (currentCount >= MAX_TOTAL) {
            alert(`이미 ${MAX_TOTAL}장의 사진이 등록되어 있습니다.`);
            return;
        }

        const remainingCount = MAX_TOTAL - currentCount;
        const slicedFiles = filesToUpload.slice(0, remainingCount);

        // Optimistic update with Blob URLs
        const tempItems = slicedFiles.map(file => ({
            id: `temp-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
            url: URL.createObjectURL(file),
            file
        }));

        setGallery([...gallery, ...tempItems.map(({ id, url }) => ({ id, url }))]);
        e.target.value = '';

        // Background Upload
        try {
            const { uploadImage } = await import('@/utils/upload');

            for (const item of tempItems) {
                try {
                    const publicUrl = await uploadImage(item.file, 'images', 'gallery');
                    setGallery(prev => prev.map(g => g.id === item.id ? { ...g, url: publicUrl } : g));
                    URL.revokeObjectURL(item.url);
                } catch (error) {
                    console.error('Upload failed', error);
                    setGallery(prev => prev.filter(g => g.id !== item.id));
                }
            }
        } catch (error) {
            console.error('Failed to import upload module', error);
        }
    };

    const handleRemove = useCallback((id: string) => {
        setGallery(gallery.filter(item => item.id !== id));
    }, [gallery, setGallery]);

    const handleDragStart = useCallback((event: DragStartEvent) => {
        setActiveId(event.active.id as string);
    }, []);

    const handleDragEnd = useCallback((event: DragEndEvent) => {
        const { active, over } = event;
        if (over && active.id !== over.id) {
            const oldIndex = gallery.findIndex(item => item.id === active.id);
            const newIndex = gallery.findIndex(item => item.id === over.id);
            setGallery(arrayMove(gallery, oldIndex, newIndex));
        }
        setActiveId(null);
    }, [gallery, setGallery]);

    const activeImage = useMemo(() =>
        gallery.find(item => item.id === activeId),
        [gallery, activeId]
    );

    return (
        <AccordionItem
            title="웨딩 갤러리"
            icon={ImageIcon}
            isOpen={isOpen}
            onToggle={onToggle}
            isCompleted={gallery.length > 0}
        >
            <div className={styles.container}>
                <Field label="갤러리 제목">
                    <TextField
                        type="text"
                        value={galleryTitle}
                        onChange={(e) => setGalleryTitle(e.target.value)}
                        placeholder="예: 웨딩 갤러리"
                    />
                </Field>

                <Field
                    label={
                        <div className={styles.counter}>
                            <Label className="!mb-0">사진 관리</Label>
                            <span className={styles.countText}>
                                <span style={{ color: theme.accentColor }}>{gallery.length}</span>
                                <span className={styles.countTotal}> / 30</span>
                            </span>
                        </div>
                    }
                >
                    <div className={styles.galleryManager}>
                        <DndContext
                            sensors={sensors}
                            collisionDetection={closestCenter}
                            onDragStart={handleDragStart}
                            onDragEnd={handleDragEnd}
                        >
                            <SortableContext
                                items={gallery.map(g => g.id)}
                                strategy={rectSortingStrategy}
                            >
                                <div className={styles.grid}>
                                    {gallery.map((item) => (
                                        <SortableItem
                                            key={item.id}
                                            id={item.id}
                                            url={item.url}
                                            onRemove={handleRemove}
                                        />
                                    ))}
                                    {gallery.length < 30 && (
                                        <div className={styles.uploadItem} onClick={handleUploadClick}>
                                            <Plus size={24} className={styles.uploadIcon} />
                                            <input
                                                type="file"
                                                ref={fileInputRef}
                                                multiple
                                                accept="image/*"
                                                className={styles.hiddenInput}
                                                onChange={handleFileChange}
                                            />
                                        </div>
                                    )}
                                </div>
                            </SortableContext>

                            <DragOverlay dropAnimation={{
                                sideEffects: defaultDropAnimationSideEffects({
                                    styles: {
                                        active: { opacity: '0.4' }
                                    }
                                })
                            }}>
                                {activeId && activeImage ? (
                                    <div className={styles.overlay}>
                                        <Image
                                            src={activeImage.url}
                                            alt=""
                                            fill
                                            unoptimized
                                            className={styles.imageOverlay}
                                        />
                                    </div>
                                ) : null}
                            </DragOverlay>
                        </DndContext>

                        <div className={styles.infoBox}>
                            <Info size={14} className={styles.infoIcon} />
                            <span>사진을 드래그하여 순서를 변경할 수 있습니다. 최대 30장까지 등록 가능합니다.</span>
                        </div>
                    </div>
                </Field>

                <Field label="전시 형태">
                    <SegmentedControl
                        value={galleryType}
                        options={[
                            { label: '스와이퍼', value: 'swiper' },
